import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { removeItem,addQuantity,subtractQuantity, addToHistory} from './actions/cartActions'
import web3 from '../web3';
import buy from '../buy';

class History extends Component{

    state = {
        queryOrderByAddress: '', 
        getContractBalance: '',
        idProduct: ''
    };

    async componentDidMount() {
        const account = await web3.eth.getAccounts();
        console.log(account[0]);

        const queryOrderByAddress = await buy.methods.getOrderBuyer(account[0]).call();
        const getContractBalance = await buy.methods.getContractBalance().call();

        const idProduct = queryOrderByAddress[2];

        this.setState({ 
            queryOrderByAddress,  
            getContractBalance,
            idProduct
        });

        console.log(idProduct);
        console.log("Contract Balance : " + getContractBalance);
    }

    //to remove the item completely
    handleRemove = (id)=>{

