import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { removeItem,addQuantity,subtractQuantity, addToHistory} from './actions/cartActions'
import web3 from '../web3';
import buy from '../buy';

class History extends Component{

    state = {
        queryOrderByAddress: '', 
        getContractBalance: '',
        idProduct: ''
    };

    async componentDidMount() {
        const account = await web3.eth.getAccounts();
        console.log(account[0]);

        const queryOrderByAddress = await buy.methods.getOrderBuyer(account[0]).call();
        const getContractBalance = await buy.methods.getContractBalance().call();

        const idProduct = queryOrderByAddress[2];

        this.setState({ 
            queryOrderByAddress,  
            getContractBalance,
            idProduct
        });

        console.log(idProduct);
        console.log("Contract Balance : " + getContractBalance);
    }

    //to remove the item completely
    handleRemove = (id)=>{

    }

    handleChecked = (e) => {

    }

   check = (total)=>{
       let newDate = new Date()
       let date = newDate.getDate();
       let month = newDate.getMonth();
       let year = newDate.getFullYear();
       let hour = newDate.getHours();
       let second = newDate.getSeconds();
       let milisecond = newDate.getMilliseconds();

       console.log(date + "" + month + "" + year + "" + hour + "" + second + "" + milisecond);
   }

    handleClick = (id)=>{
        this.props.addToHistory(id)
    }

    render(){

        var i;
        var itemss = [];

        for (i = 0; i < this.props.items.length; i++) { 

            if (this.props.items[i].id == this.state.idProduct) {
                itemss.push({
                    id: this.props.items[i].id,
                    img: this.props.items[i].img,
                    title: this.props.items[i].title
                });
            }
        }

        console.log(itemss.length);

        let addedItems = this.props.itemss.length ?
            (  
                this.props.itemss.map( item=>  {
                    return(
                        <li className="collection-item avatar" key={item.id}>
                            <div className="item-img"> 
                                <img src={item.img} alt={item.img} className=""/>
                            </div>

                            <div className="item-desc">
                                <span className="title">{item.title}</span>
                                <p>{item.desc}</p>
                                <p><b>Price: {item.price}$</b></p> 
                                <p>
                                    <b>Quantity: {item.quantity}</b> 
                                </p>
                                <div className="add-remove">
                                    <Link to="/cart"><i className="material-icons" onClick={()=>{this.handleAddQuantity(item.id)}}>arrow_drop_up</i></Link>
                                    <Link to="/cart"><i className="material-icons" onClick={()=>{this.handleSubtractQuantity(item.id)}}>arrow_drop_down</i></Link>
                                </div>
                                <div className="add-remove">
                                    <label>
                                        <input type="checkbox" ref="shipping" onChange= {this.handleChecked} />
                                        <span>Shipping(+6$)</span>
                                    </label>
                                </div>

                                <p>
                                    <b>Total: {this.props.total} $</b>
                                </p>
                                <p>
                                <br/>
                                Address
                                <input type="text" name="address" value={this.state.address} onChange={this.handleChange}/>
                                </p>

                                <p>
                                <br/>
                                Phone Number
                                <input type="number" name="phonenumber" value={this.state.phonumber} onChange={this.handleChange}/>
                                </p>

                                <p>
                                <br/>
                                Note
                                <input type="text" name="note" value={this.state.note} onChange={this.handleChange}/>
                                </p>

                                <button className="waves-effect waves-light btn pink remove" onClick={()=>{this.handleRemove(item.id)}}>Remove</button>
                                <button className="waves-effect waves-light btn" onClick={()=>{this.handleClick(item.id, item.title, item.price, item.quantity)}}>Checkout</button>
                            </div>        
                        </li> 
                    )
                })
            ):

             (
                <p>Nothing.</p>
             )
       return(
            <div className="container">
                <div className="cart">
                    <h5>You have ordered:</h5>
                    <ul className="collection">
                        {addedItems}
                    </ul>
                </div>          
            </div>
       )
    }
}


const mapStateToProps = (state)=>{
    return{
        items: state.items,
        addedItems: state.addedItems
    }
}
const mapDispatchToProps = (dispatch)=>{
    return{
        addShipping: ()=>{dispatch({type: 'ADD_SHIPPING'})},
        substractShipping: ()=>{dispatch({type: 'SUB_SHIPPING'})},
        removeItem: (id)=>{dispatch(removeItem(id))},
        addQuantity: (id)=>{dispatch(addQuantity(id))},
        subtractQuantity: (id)=>{dispatch(subtractQuantity(id))},
        addToHistory: (id)=>{dispatch(addToHistory(id))}
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(History)
