import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { removeItem,addQuantity,subtractQuantity, addToHistory} from './actions/cartActions'
class History extends Component{
    //to remove the item completely
    handleRemove = (id)=>{

    }

    handleChecked = (e) => {

    }

   check = (total)=>{
       let newDate = new Date()
       let date = newDate.getDate();
       let month = newDate.getMonth();
       let year = newDate.getFullYear();
       let hour = newDate.getHours();
       let second = newDate.getSeconds();
       let milisecond = newDate.getMilliseconds();

       console.log(date + "" + month + "" + year + "" + hour + "" + second + "" + milisecond);
   }

    handleClick = (id)=>{
        this.props.addToHistory(id)
    }

    render(){

        let addedItemsHistory = this.props.items.length ?
            (  
                this.props.items.map( item=>  {
                    return(

                        <li className="collection-item avatar" key={item.id}>
                                    <div className="item-img"> 
                                        <img src={item.img} alt={item.img} className=""/>
                                    </div>

                                    <div className="item-desc">
                                        <span className="title">{item.title}</span>
                                        <p>{item.desc}</p>
                                        <p><b>Price: {item.price}$</b></p> 
                                        <p>
                                            <b>Quantity: {item.quantity}</b> 
                                        </p>
                                        <div className="add-remove">
                                            <label>
                                                <input type="checkbox" ref="shipping" onChange= {this.handleChecked} />
                                                <span>Shipping(+6$)</span>
                                            </label>
                                        </div>

                                        <p>
                                            <b>Total: {this.props.total} $</b>
                                        </p>
                                        <p>
                                            <textarea rows="4" cols="50" name="comment" form="usrform" defaultValue="This is a description."></textarea>
                                        </p>

                                        <button className="waves-effect waves-light btn pink remove" onClick={()=>{this.handleRemove(item.id)}}>Tracking</button>
                                        <button className="waves-effect waves-light btn" onClick={()=>{this.handleClick(item.id)}}>Delivered</button>
                                    </div>

                                </li>

                    )
                })
            ):

             (
                <p>Nothing.</p>
             )
       return(
            <div className="container">
                <div className="cart">
                    <h5>You have ordered:</h5>
                    <ul className="collection">
                        {addedItemsHistory}
                    </ul>
                </div>          
            </div>
       )
    }
}


const mapStateToProps = (state)=>{
    return{
        items: state.addedItemsHistory,
        addedItemsHistory: state.addedItemsHistory,
        total: state.total
        //addedItems: state.addedItems
    }
}
const mapDispatchToProps = (dispatch)=>{
    return{
        addShipping: ()=>{dispatch({type: 'ADD_SHIPPING'})},
        substractShipping: ()=>{dispatch({type: 'SUB_SHIPPING'})},
        removeItem: (id)=>{dispatch(removeItem(id))},
        addQuantity: (id)=>{dispatch(addQuantity(id))},
        subtractQuantity: (id)=>{dispatch(subtractQuantity(id))},
        addToHistory: (id)=>{dispatch(addToHistory(id))}
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(History)
