import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { removeItem,addQuantity,subtractQuantity, addToHistory} from './actions/cartActions'
import web3 from '../web3';
import buy from '../buy';

class HomeScreen extends Component{

    state = {
        queryOrderByAddress: '', 
        getContractBalance: '',
        orderNo: '',
        idProduct: '',
        product: '',
        qty: '',
        location: '',
        phoneNumber: '',
        priceProduct: '',
        description: '',
        priceCourier: '',
        status: '',
        totalPrice: '',
    };

    async componentDidMount() {
        const account = await web3.eth.getAccounts();
        console.log(account[0]);

        const queryOrderByAddress = await buy.methods.getOrderBuyer(account[0]).call();
        const getContractBalance = await buy.methods.getContractBalance().call();

        const orderNo = queryOrderByAddress[0];
        const idProduct = queryOrderByAddress[2];
        const product = queryOrderByAddress[3];
        const qty = queryOrderByAddress[4];
        const location = queryOrderByAddress[5];
        const phoneNumber = queryOrderByAddress[6];
        const priceProduct = (web3.utils.fromWei(queryOrderByAddress[7].toString(), 'ether') * 1);
        const description = queryOrderByAddress[8];
        const priceCourier = (web3.utils.fromWei(queryOrderByAddress[9].toString(), 'ether') * 1);

        if (queryOrderByAddress[10] == 1) {
            const status = "Barang belum diproses oleh penjual";
            this.setState({ 
                status
            });
        } else if (queryOrderByAddress[10] == 2) {
            const status = "Barang Sedang dikirim oleh Kurir";
            this.setState({ 
                status
            });
        } else if (queryOrderByAddress[10] == 2) {
            const status = "Barang sudah diterima";
            this.setState({ 
                status
            });
        }
        const totalPrice = priceProduct + priceCourier;
        console.log(totalPrice);

        this.setState({ 
            queryOrderByAddress,  
            getContractBalance,
            orderNo,
            idProduct,
            product,
            qty,
            location,
            phoneNumber,
            priceProduct,
            description,
            priceCourier,
            totalPrice
        });

        console.log(idProduct);
        console.log("Contract Balance : " + getContractBalance);
    }

    //to remove the item completely
    handleRemove = (id)=>{

    }

    handleChecked = (e) => {

    }

   check = (total)=>{
       let newDate = new Date()
       let date = newDate.getDate();
       let month = newDate.getMonth();
       let year = newDate.getFullYear();
       let hour = newDate.getHours();
       let second = newDate.getSeconds();
       let milisecond = newDate.getMilliseconds();

       console.log(date + "" + month + "" + year + "" + hour + "" + second + "" + milisecond);
   }

    handleClick = async (id, title, price, quantity)=>{
        const accounts = await web3.eth.getAccounts();

        var trackingNo           = '';
        var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        var charactersLength = characters.length;
        for ( var i = 0; i < 20; i++ ) {
            trackingNo += characters.charAt(Math.floor(Math.random() * charactersLength));
        }
        console.log(trackingNo);

        fetch('https://api.aftership.com/v4/trackings', {
                method: 'POST',
                headers: {
                    "Content-type": "application/json",
                    "aftership-api-key": "a8695ccf-ff04-4cf2-a2d7-ba55f424786d"
                },
                body:JSON.stringify({
                    tracking: {
                        slug: "dhl",
                        tracking_number: "5773098991",
                        title: this.state.product,
                        smses: [
                            this.state.phoneNumber
                        ],
                        emails: [
                            "email@yourdomain.com"
                        ],
                        order_id: this.state.orderNo,
                        order_id_path: "http://www.aftership.com/order_id=" + this.state.orderNo,
                        custom_fields: {
                            product_name: this.state.product,
                            product_price: this.state.priceProduct
                        },
                        language: "en",
                        delivery_type: "pickup_at_store",
                        pickup_location: this.state.location,
                    }
                })
            }).then((res) => res.json())
            .then((data) =>  console.log(data))
            .catch((err)=>console.log(err))
    }

    render(){

        var i;
        var itemss = [];

        for (i = 0; i < this.props.items.length; i++) { 

            if (this.props.items[i].id == this.state.idProduct) {
                itemss.push({
                    id: this.props.items[i].id,
                    img: this.props.items[i].img,
                    title: this.props.items[i].title
                });
            }
        }

        let addedItems = itemss.length ?
            (  
                itemss.map( item=>  {
                    return(
                        <li className="collection-item avatar" key={item.id}>
                            <div className="item-img"> 
                                <img src={item.img} alt={item.img} className=""/>
                            </div>

                            <div className="item-desc">
                                <span className="title">{this.state.product}</span>
                                <p>{item.desc}</p>
                                <p><b>Harga: {this.state.priceProduct} ETH</b></p> 
                                <p>
                                    <b>Jumlah: {this.state.qty}</b> 
                                </p>
                                <div className="add-remove">
                                    <label>
                                        <span>Kurir(+{this.state.priceCourier} ETH)</span>
                                    </label>
                                </div>

                                <p>
                                    <b>Total: {this.state.totalPrice} ETH</b>
                                </p>
                                <p>
                                    <b>Status: {this.state.status}</b>
                                </p>
                                <p>
                                <br/>
                                Alamat
                                <input type="text" name="address" defaultValue={this.state.location} onChange={this.handleChange} disabled/>
                                </p>

                                <p>
                                <br/>
                                Nomor Telepon
                                <input type="number" name="phonenumber" defaultValue={this.state.phoneNumber} onChange={this.handleChange} disabled/>
                                </p>

                                <p>
                                <br/>
                                Catatan
                                <input type="text" name="note" defaultValue={this.state.description} onChange={this.handleChange} disabled/>
                                </p>

                                <button className="waves-effect waves-light btn pink remove" onClick={()=>{this.handleRemove(item.id)}}>Batalkan</button>
                                <button className="waves-effect waves-light btn" onClick={()=>{this.handleClick(item.id, item.title, item.price, item.quantity)}}>Kirim</button>
                            </div>        
                        </li> 
                    )
                })
            ):

             (
                <p>Nothing.</p>
             )
       return(
            <div className="container">
                <div className="cart">
                    <h5>Riwayat Belanja:</h5>
                    <ul className="collection">
                        {addedItems}
                    </ul>
                </div>          
            </div>
       )
    }
}


const mapStateToProps = (state)=>{
    return{
        items: state.items,
        addedItems: state.addedItems
    }
}
const mapDispatchToProps = (dispatch)=>{
    return{
        addShipping: ()=>{dispatch({type: 'ADD_SHIPPING'})},
        substractShipping: ()=>{dispatch({type: 'SUB_SHIPPING'})},
        removeItem: (id)=>{dispatch(removeItem(id))},
        addQuantity: (id)=>{dispatch(addQuantity(id))},
        subtractQuantity: (id)=>{dispatch(subtractQuantity(id))},
        addToHistory: (id)=>{dispatch(addToHistory(id))}
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(HomeScreen)
