    handleChecked = (e) => {

    }

   check = (total)=>{
       let newDate = new Date()
       let date = newDate.getDate();
       let month = newDate.getMonth();
       let year = newDate.getFullYear();
       let hour = newDate.getHours();
       let second = newDate.getSeconds();
       let milisecond = newDate.getMilliseconds();

       console.log(date + "" + month + "" + year + "" + hour + "" + second + "" + milisecond);
   }

    handleClick = (id)=>{
        this.props.addToHistory(id)
    }

    render(){
        if (this.props.items.id == this.state.idProduct) {
            let itemList = this.props.items.map(item=>{
                return(
                    <div className="card" key={item.id}>
                            <div className="card-image">
                                <img src={item.img} alt={item.title}/>
                                <span className="card-title">{item.title}</span>
                                <Link to="/cart" className="btn-floating halfway-fab waves-effect waves-light red" onClick={()=>{this.handleClick(item.id)}}><i className="material-icons">add</i></Link>
                            </div>

                            <div className="card-content">
                                <p>{item.desc}</p>
                                <p><b>Price: {item.price}$</b></p>
                            </div>
                     </div>

                )
            })          
           return(
                <div className="container">
                    <div className="cart">
                        <h5>You have ordered:</h5>
                        <ul className="collection">
                            {itemList}
                        </ul>
                    </div>          
                </div>
           )
        } else {
            let itemList = this.props.addedItems.map(item=>{
                return(
                    <div className="card" key={item.id}>
                            <div className="card-image">
                                <img src={item.img} alt={item.title}/>
                                <span className="card-title">{item.title}</span>
                                <Link to="/cart" className="btn-floating halfway-fab waves-effect waves-light red" onClick={()=>{this.handleClick(item.id)}}><i className="material-icons">add</i></Link>
                            </div>

                            <div className="card-content">
                                <p>{item.desc}</p>
                                <p><b>Price: {item.price}$</b></p>
                            </div>
                     </div>

                )
            })          
           return(
                <div className="container">
                    <div className="cart">
                        <h5>You have ordered:</h5>
                        <ul className="collection">
                            {itemList}
                        </ul>
                    </div>          
                </div>
           )
        }
    }
}


const mapStateToProps = (state)=>{
    return{
        items: state.items,
        addedItems: state.addedItems
    }
}
const mapDispatchToProps = (dispatch)=>{
    return{
        addShipping: ()=>{dispatch({type: 'ADD_SHIPPING'})},
        substractShipping: ()=>{dispatch({type: 'SUB_SHIPPING'})},
        removeItem: (id)=>{dispatch(removeItem(id))},
        addQuantity: (id)=>{dispatch(addQuantity(id))},
        subtractQuantity: (id)=>{dispatch(subtractQuantity(id))},
        addToHistory: (id)=>{dispatch(addToHistory(id))}
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(History)
