import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { removeItem,addQuantity,subtractQuantity, addToHistory} from './actions/cartActions'

class Cart extends Component{
    //to remove the item completely
    handleRemove = (id)=>{
        this.props.removeItem(id);
    }
    //to add the quantity
    handleAddQuantity = (id)=>{
        this.props.addQuantity(id);
    }
    //to substruct from the quantity
    handleSubtractQuantity = (id)=>{
        this.props.subtractQuantity(id);
    }

   handleChecked = (e) => {
       if(e.target.checked){
           this.props.addShipping();
       }
       else{
           this.props.substractShipping();
       }
   }

    
    handleClick = (id, title, price, quantity)=>{
        //this.props.addToHistory(id)
        
        let newDate = new Date()
        let date = newDate.getDate();
        let month = newDate.getMonth();
        let year = newDate.getFullYear();
        let hour = newDate.getHours();
        let second = newDate.getSeconds();
        let milisecond = newDate.getMilliseconds();
       
        /*this.purchaseThisItem({
            'orderNo': date + "" + month + "" + year + "" + hour + "" + second + "" + milisecond,
            'product': web3.toUtf8(String(itemDetails).split(',')[0]),
            'priceProduct': parseInt(String(itemDetails).split(',')[1]),
            'quantity': 1,
            'location': parseInt(String(itemDetails).split(',')[1]),
            'phoneNumber': parseInt(String(itemDetails).split(',')[1]),
            'description': parseInt(String(itemDetails).split(',')[1]),
            'courier': parseInt(String(itemDetails).split(',')[1]),
            'priceCourier': parseInt(String(itemDetails).split(',')[1]),
            'deliveryDate': parseInt(String(itemDetails).split(',')[1]),
        });*/

        //console.log(date + "" + month + "" + year + "" + hour + "" + second + "" + milisecond);
        //console.log(id + "" + title + "" + price + "" + quantity);
    }

    render(){
              
        let addedItems = this.props.items.length ?
            (  
                this.props.items.map( item=>  {
                    return(
                       
                        <li className="collection-item avatar" key={item.id}>
                                    <div className="item-img"> 
                                        <img src={item.img} alt={item.img} className=""/>
                                    </div>
                                
                                    <div className="item-desc">
                                        <span className="title">{item.title}</span>
                                        <p>{item.desc}</p>
                                        <p><b>Price: {item.price}$</b></p> 
                                        <p>
                                            <b>Quantity: {item.quantity}</b> 
                                        </p>
                                        <div className="add-remove">
                                            <Link to="/cart"><i className="material-icons" onClick={()=>{this.handleAddQuantity(item.id)}}>arrow_drop_up</i></Link>
                                            <Link to="/cart"><i className="material-icons" onClick={()=>{this.handleSubtractQuantity(item.id)}}>arrow_drop_down</i></Link>
                                        </div>
                                        <div className="add-remove">
                                            <label>
                                                <input type="checkbox" ref="shipping" onChange= {this.handleChecked} />
                                                <span>Shipping(+6$)</span>
                                            </label>
                                        </div>

                                        <p>
                                            <b>Total: {this.props.total} $</b>
                                        </p>
                                        <p>
                                            <textarea rows="4" cols="50" name="comment" form="usrform" defaultValue="Description"></textarea>
                                        </p>
                                        <p>
                                            <textarea rows="4" cols="50" name="comment" form="usrform" defaultValue="Address"></textarea>
                                        </p>
                                        <p>
                                            <textarea rows="1" cols="50" name="comment" form="usrform" defaultValue="Phone Number"></textarea>
                                        </p>

                                        <button className="waves-effect waves-light btn pink remove" onClick={()=>{this.handleRemove(item.id)}}>Remove</button>
                                        <Link to="/history" className="waves-effect waves-light btn" onClick={()=>{this.handleClick(item.id, item.title, item.price, item.quantity)}}>Checkout</Link>
                                    </div>
                                    
                                </li>
                         
                    )
                })
            ):

             (
                <p>Nothing.</p>
             )
       return(
            <div className="container">
                <div className="cart">
                    <h5>You have ordered:</h5>
                    <ul className="collection">
                        {addedItems}
                    </ul>
                </div>          
            </div>
       )
    }
}


const mapStateToProps = (state)=>{
    return{
        items: state.addedItems,
        addedItems: state.addedItems,
        total: state.total
        //addedItems: state.addedItems
    }
}
const mapDispatchToProps = (dispatch)=>{
    return{
        addShipping: ()=>{dispatch({type: 'ADD_SHIPPING'})},
        substractShipping: ()=>{dispatch({type: 'SUB_SHIPPING'})},
        removeItem: (id)=>{dispatch(removeItem(id))},
        addQuantity: (id)=>{dispatch(addQuantity(id))},
        subtractQuantity: (id)=>{dispatch(subtractQuantity(id))},
        addToHistory: (id)=>{dispatch(addToHistory(id))}
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Cart)
